  function createPageContent(idx){
    let div = document.createElement('div');
    let img = document.createElement('img');
    let p = document.createElement('p');

    div.classList.add('content');
    img.src = pages[idx].img;
    p.innerText = pages[idx].text;
    div.append(p);
    div.append(img);
    return div;
  }


  return (
    <div className={styles.book}>

    <div id="leftarrow" className={styles.arrow} onClick={() => turnLeft()}>
      <span className="material-icons">
        arrow_back
      </span>
    </div>

    <div className={styles.scene}>
      <div id="prev" className={styles.hidden}>
        <div className={styles.content}></div>
    </div>

      <div className={`${styles.card} ${styles.left} ${styles.hidden}`} onClick={() => turnLeft()}>
        <div className={`${styles.card__face} ${styles.card__face__front}`}>
          <div className={styles.content}></div>
        </div>
        <div className={`${styles.card__face} ${styles.card__face__back}`}>
          <div className={styles.content}></div>
        </div>
      </div>

      <div className={`${styles.card} ${styles.right} ${styles.is_flipped}`} onClick={() => turnRight()}>
        <div className={`${styles.card__face} ${styles.card__face__front}`}>
          <div className={styles.content}>
            <p>
              hola pg 2
            </p>
            <img
              alt="property"
              src="https://res.cloudinary.com/dftvewldz/image/upload/c_scale,w_450/v1538425969/collages/digital/c6.jpg"
            />
          </div>
        </div>
        <div className={`${styles.card__face} ${styles.card__face__back}`}>
          <div className={styles.content}>
            <p>
              hewwo pg 1
            </p>
            <img
              alt="property"
              src="https://res.cloudinary.com/dftvewldz/image/upload/c_scale,w_450/v1538425968/collages/digital/c11.jpg"
            />
          </div>
        </div>
      </div>

      <div id="next">
        <div className={styles.content}>
          <p>
            this is pg 3 :)
          </p>
          <img
            alt="property"
            src="https://res.cloudinary.com/dftvewldz/image/upload/c_scale,w_450/v1538425943/collages/digital/c8.jpg"
          />
        </div>
      </div>
    </div>

    <div id="right-arrow" className={styles.arrow} onClick={() => turnRight()}>
      <span className="material-icons">
        arrow_forward
      </span>
    </div>

    </div>
