import React, {useState} from 'react'
import Image from 'next/image'
import FilterButton from '../components/FilterButton'
import styles from './art.module.css'

class Art extends React.Component {
  constructor() {
    super()
    this.state = {
      tags: ["art", "code", "research", "game", "vr/ar", "writing", "illustration", "freelance"],
      filterTags: [],
      projects: [
        {
          name: "Copy/Paste",
          year: "2018",
          desc: "Series of mixed media collages made from children's books then digitally altered",
          image: "https://res.cloudinary.com/dftvewldz/image/upload/h_250/v1538425934/collages/digital/c1.jpg",
          alt: "collage",
          tags: ["art"]
        },
        {
          name: "Make it Neat!",
          year: "2019",
          desc: "VR bartending game made for physical therapy research- practicing wrist rotation.",
          image: "https://res.cloudinary.com/dftvewldz/image/upload/h_250/v1538425934/collages/digital/c1.jpg",
          tags: ["art", "code", "hci", "game"]
      }],
      filteredProjects: [
        {
          name: "Make it Neat!",
          year: "2019",
          desc: "VR bartending game made for physical therapy research- practicing wrist rotation motions",
          image: "https://res.cloudinary.com/dftvewldz/image/upload/h_250/v1538425934/collages/digital/c1.jpg",
          tags: ["art", "code", "hci", "game"]
      }
      ]
    }
  }

  changeFilter(tag){
    //add tag if not already in filter
    if (!this.state.filterTags.includes(tag)){
      let copy = this.state.filterTags;
      copy.push(tag);
      this.setState({
        filterTags: copy
      });
    } else {
      //remove tag if already in filter
      let copy = this.state.filterTags;
      let index = copy.indexOf(tag);
      copy.splice(index, 1);

      this.setState({
        filterTags: copy
      });
    }
  }

  filterResults(tag){
    this.changeFilter(tag);

    let filteredProjects = this.state.projects.filter((p) => {
      return this.state.filterTags.every( t => p.tags.includes(t) )
     });

    this.setState({
      filteredProjects: filteredProjects
    });
  }

  componentDidMount() {
  }

  componentWillUnmount() {
  }

	render() {
    return (
  		<div className={styles.projectPageContainer}>
        <div className={styles.filterTagContainer}>
        <div>Filter Projects</div>
        {this.state.tags.map((tag, i) => {
            return(
              <FilterButton
                name={tag}
                key={tag}
                onChange={this.filterResults.bind(this, tag)}
              >
              </FilterButton>

            )
          })}
        </div>

      <div className={styles.projectsContainer}>
        {this.state.filteredProjects.map((project, i) => {
            return (
              <div key={`${project.name}`} >

                <div className={styles.topBar}></div>

                <div className={styles.projectContainer}>
                  <div className={styles.projectTextContainer}>
                    <div className={styles.projectNameContainer}>
                      <span className={styles.projectName}>{project.name}</span>
                      <span className={styles.projectYear}>  {project.year}</span>
                    </div>
                    <div className={styles.projectDesc}>
                    {project.desc}
                    </div>
                  </div>
                  <img className={styles.previewImage} src={project.image} alt={project.alt} />
                  </div>
                </div>

            )
        })}
      </div>
  		</div>
    )
	}
}


export default Art;
