import React, {useState} from 'react'
import Image from 'next/image'
import FilterButton from '../components/FilterButton'
import styles from './art.module.css'

class Art extends React.Component {
  constructor() {
    super()
    this.state = {
      tags: ["art", "code", "freelance", "research", "game", "vr/ar", "writing"],
      filterTags: [],
      projects: [
        {
          name: "Copy/Paste",
          year: "2018",
          desc: "Series of mixed media collages made from children's books then digitally altered",
          image: "https://res.cloudinary.com/dftvewldz/image/upload/h_250/v1538425934/collages/digital/c1.jpg",
          alt: "collage",
          tags: ["art"]
      },{
          name: "ICT VR Research",
          year: "2019",
          desc: "VR Games created for the Institute of Creative Technology for physical therapy resesarch. Uses hand tracking to track how patients practice various arm motions.",
          image: "https://res.cloudinary.com/dftvewldz/image/upload/v1609780336/games/make-it-neat-preview.gif",
          tags: ["code", "research", "game"]
      },{
        name: "Art-Discontent",
        year: "2020",
        desc: "Profiles on young artistic practices. Website, interviews, and articles all created by me.",
        image: "https://res.cloudinary.com/dftvewldz/image/upload/v1609866701/previews/art-discontent-preview.gif",
        tags: ["art", "code", "writing"]
      },{
        name: "Off the Record",
        year: "2020",
        desc: "Text based game about the experience of journalists of color for Study Hall. Interface created by me.",
        image: "https://res.cloudinary.com/dftvewldz/image/upload/c_scale,w_667/v1609866836/off-the-record-preview.png",
        tags: ["freelance", "game", "code"]
      },{
        name: "Vaporsquare",
        year: "2018",
        desc: "An interactive installation exploring promised technological paradise, obsolescense, and unknown systems.",
        image: "https://res.cloudinary.com/dftvewldz/image/upload/v1535237063/vaporsquare/cmr2.gif",
        tags: ["freelance", "game", "code"]
      },{
        name: "Publications",
        year: "2020",
        desc: "Various prints, zines, and sketchbooks in an online format.",
        image: "https://res.cloudinary.com/dftvewldz/image/upload/c_scale,w_667/v1609866836/off-the-record-preview.png",
        tags: ["art", "writing"]
      },{
        name: "Alexa, the Acousmatic Voice (a sound sequencer)",
        year: "2020",
        desc: "Web sound sequencer and essay about the source of Amazon's Alexa",
        image: "https://res.cloudinary.com/dftvewldz/image/upload/v1609867855/games/acousmaticalexa.png",
        tags: ["art", "writing", "code"]
      },{
        name: "Vaporwave, the Eclectic Aesthetic about Time (an image generator)",
        year: "2019",
        desc: "Web Image Generator and essay about the visual inspirations of Vaporwave aesthetics",
        image: "https://res.cloudinary.com/dftvewldz/image/upload/v1580524622/games/vaporwavegen.png",
        tags: ["art", "writing", "code"]
      }],
      filteredProjects: [
      ]
    }
  }

  changeFilter(tag){
    //add tag if not already in filter
    if (!this.state.filterTags.includes(tag)){
      let copy = this.state.filterTags;
      copy.push(tag);
      this.setState({
        filterTags: copy
      });
    } else {
      //remove tag if already in filter
      let copy = this.state.filterTags;
      let index = copy.indexOf(tag);
      copy.splice(index, 1);

      this.setState({
        filterTags: copy
      });
    }
  }

  filterResults(tag){
    this.changeFilter(tag);

    let filteredProjects = this.state.projects.filter((p) => {
      return this.state.filterTags.every( t => p.tags.includes(t) )
     });

    this.setState({
      filteredProjects: filteredProjects
    });
  }

  componentDidMount() {
  }

  componentWillUnmount() {
  }

	render() {
    return (
  		<div className={styles.projectPageContainer}>
        <div className={styles.filterTagContainer}>
        <div>Filter Projects</div>
        {this.state.tags.map((tag, i) => {
            return(
              <FilterButton
                name={tag}
                key={tag}
                onChange={this.filterResults.bind(this, tag)}
              >
              </FilterButton>

            )
          })}
        </div>

      <div className={styles.projectsContainer}>
        {this.state.filteredProjects.map((project, i) => {
            return (
              <div key={`${project.name}`} >

                <div className={styles.topBar}></div>

                <div className={styles.projectContainer}>
                  <div className={styles.projectTextContainer}>
                    <div className={styles.projectNameContainer}>
                      <span className={styles.projectName}>{project.name}</span>
                      <span className={styles.projectYear}>  {project.year}</span>
                    </div>
                    <div className={styles.projectDesc}>
                    {project.desc}
                    </div>
                  </div>
                  <img className={styles.previewImage} src={project.image} alt={project.alt} />
                  </div>
                </div>

            )
        })}
      </div>
  		</div>
    )
	}
}


export default Art;
