const Publications = () => {
  const currIdx = 0;
  const isTurning = false;
  console.log(styles.arrow)

  return (
    <div class="book">
    <div id="leftarrow" class="arrow" onclick="turnLeft()">
      <span class="material-icons">
        arrow_back
      </span>
    </div>

    <div class="scene scene--card">
      <div id="prev" class="hidden">
        <div class="content"></div>
    </div>

      <div class="card left hidden">
        <div class="card__face card__face--front">
