import styles from './index.module.css'
import Link from 'next/Link'
import styled from 'styled-components'


const PassLink = ({ as, children, className, href }) => (
  <Link href={href} as={as} passHref>
    <a className={className}>{children}</a>
  </Link>
)

const StyledLink = styled(PassLink)`
	color: ${props => props.color};
	background-image: linear-gradient(0deg, rgba(131,58,180,1) 0%, rgba(253,29,29,1) 50%, rgba(252,176,69,1) 100%);
  background-repeat: no-repeat;
  background-size: 100% 0em;
  background-position: 0 100%;
	text-decoration: none;
  transition: background-size 0.25s ease-in, color .1s ease-in;
	padding: 0px 5px;
  &:hover {
    color: ${props => props.hoverColor};
		background-size: 100% 100%;
	  cursor: pointer;
  }
`

class Index extends React.Component {
	render() {
    return (
  		<div>
  			<div className={styles.line}>
  				<div>🎨</div>
  				<div>💻</div>
  			</div>
  			<div className={styles.line}>
  				<div className={styles.font3}>
  					MARY
  				</div>
  				<img className={styles.lineImage} alt="small kermit" src="https://res.cloudinary.com/dftvewldz/image/upload/v1535237604/kermits/3.png" />
  				<div className={styles.font3}>
  					TRUONG
  				</div>
  					<img className={styles.lineImage} alt="computer" src="https://res.cloudinary.com/dftvewldz/image/upload/v1535760468/publications/artiststatement/3.gif" />
  			</div>

  			<div className={styles.line}>
  			<StyledLink href='/art'
  				className={styles.font1}
  				color={`white`}
  				hoverColor={`blue`}
  			>
  				ART
  			</StyledLink>
  				<img className={styles.lineImage} alt="computer" src="https://res.cloudinary.com/dftvewldz/image/upload/v1535760468/publications/artiststatement/3.gif" />
  				<StyledLink href='/code'
  					className={styles.font2}
  					color={`white`}
  					hoverColor={`blue`}
  				>
  					TECHNOLOGY
  				</StyledLink>
  			</div>

  			<div className={styles.line}>
  				<div className={styles.font2}>
  					RESUMÉ
  				</div>
  				<div className={styles.font4}>
  					ABOUT
  				</div>
  			</div>

  				<div>
  					<span className={styles.font5}>THANK YOU</span>
  					<span className={styles.font6}>THANK YOU</span>
  					<span className={styles.font5}>THANK YOU<br/></span>
  					<div className={styles.font7}>
  						HAVE A NICE DAY!
  					</div>
  				</div>

  			<div className={styles.bio}>
  				hello, i'm mary! <br/>
  				i studied computer science and art theory and practice <br/>
  				i think a lot about technology, contemporary art, and games as an artistic medium<br/>
  				i've dabbled in virtual & augmented reality, hci research, printmaking, writing fanfiction, journalism, and game design<br/>
  				the next thing i want to finish is a web-based incremental game <br/>
  			</div>
  		</div>
    )
	}
}


export default Index;
